"use strict";

var path = require("path");

var sysPrint = console.log;
var cloudMap = {
  qiniu: "七牛",
  wantu: "顽兔"
};

module.exports = {
  empty: function empty() {
    sysPrint("\n没有可上传的文件...");
  },
  files: function files(_files, dir) {
    sysPrint("\n可以上传的文件如下：");

    _files.forEach(function (f) {
      sysPrint(path.join(dir, f), "\r");
    });
  },
  prepared: function prepared(cloudType) {
    sysPrint("\n将要上传到" + cloudMap[cloudType]);
  },
  abort: function abort() {
    sysPrint("\n放弃上传");
  },
  uploading: function uploading(localFile, cloudType) {
    sysPrint("[INFO] 正在上传到" + cloudMap[cloudType] + " >>> " + localFile + "\r");
  },
  uploaded: function uploaded(fileUrl, cloudType) {
    sysPrint("[INFO] 文件已上传到" + cloudMap[cloudType] + " >>> " + fileUrl + "\r");
  }
};